import React, { Component, useState } from 'react'
import axios from '../axios-orders'

function Contact() {

    const [campos, setCampos] = useState({
        nome: '',
        email: '',
        mensagem: '',
        anexo: ''
    });
    function handleInputChange(event) {
        if (event.target.name === "anexo")
            campos[event.target.name] = event.target.files[0];
        else
            campos[event.target.name] = event.target.value;
        setCampos(campos);
    }

    function send() {
        const formData = new FormData();
        Object.keys(campos).forEach(key => formData.append(key, campos[key]));
        axios.post('http://localhost:3030/send',
            formData,
            {
                headers: {
                    "Content-Type": `multipart/form-data; boundary=${formData._boundary}`,
                }
            })
            .then(response => { console.log(response.data); })
    }

    function handleFormSubmit(event) {
        event.preventDefault();
        console.log(campos);
        send(campos);
        alert("Email enviado com sucesso!");
        window.location.reload();
    }

    return (
        // Contact section start
        <section className="contact">
            <div className="container">
                <div className="row">
                    <div className="col-lg-8 col-md-10 m-auto">
                        <div className="sec-heading">
                            <h3 className="sec-title">Entre em contato</h3>
                            <p>Queremos te ouvir!</p>
                        </div>
                    </div>
                </div>
                <div className="row">
                    <div className="col-lg-10 col-md-12 m-auto">
                        <div className="row">
                            <div className="col-md-4">
                                <address>
                                    <span className="ti-map-alt"></span>
                                        Rua Presidente João Pessoa 322 loja 105, Icaraí <br />
                                        Niterói, RJ
                                    </address>
                                <address>
                                    <span className="ti-email"></span>
                                    <a href="mailto:example@leospa.com">contato@divasbeleza.com.br</a>
                                    <br /><br />
                                </address>
                                <address>
                                    <span className="ti-tablet"></span>
                                    <a href="tel:+552136191304(02)">21 3619-1304</a>
                                    <a href="http://bit.ly/vemprodivas" target="blank">21 99227-7167</a>
                                </address>
                            </div>
                            <div className="col-md-7 offset-md-1">
                                <form className="contact-form" onSubmit={handleFormSubmit}>


                                    <input
                                        type="text"
                                        name="nome"
                                        placeholder="Nome"
                                        onChange={handleInputChange}
                                        id="nome"
                                        required
                                    />
                                    <input
                                        type="email"
                                        name="email"
                                        placeholder="Email"
                                        onChange={handleInputChange}
                                        id="email"
                                        required
                                    />
                                    <textarea
                                        name="mensagem"
                                        placeholder="Escreva sua mensagem..."
                                        onChange={handleInputChange}
                                        id="mensagem"
                                        required
                                    ></textarea>
                                    {/* <label htmlFor="anexo">Anexo</label>
                                    <input type="file" id="anexo" name="anexo" /> */}
                                    <button type="submit" className="btn btn-round">Enviar</button>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </section>
        // Contact section end
    )

}

export default Contact

