import React, { Component } from 'react'
import axios from '../axios-orders'

const GOOGLE_FORM_MESSAGE_ID = 'entry.640466644'
const GOOGLE_FORM_EMAIL_ID = 'entry.484525810'
const GOOGLE_FORM_NAME_ID = 'entry.837107227'
const GOOGLE_FORM_ACTION_URL = 'https://docs.google.com/forms/u/2/d/e/1FAIpQLSfqCKImjrvYdwSSLoV-zyk9F3PG5sWx8IeSGYE2uTVfrcYk0A/formResponse'
const CORS_PROXY = 'https://cors-escape.herokuapp.com/'

class Contact extends Component {
    constructor(props){
        super(props)
        this.state = { 
          message: '', 
          email: '',
          name: '',
          sendingMessage: false,
          messageSent: false,
          messageError: false
        }
      }

      handleChange = (event) => {
        this.setState({[event.target.name]: event.target.value})
      }

      handleSubmit = (event) => {
        event.preventDefault()
        this.setState({
          sendingMessage: true
        })
        this.sendMessage()
      }
      
      handleReturnButton = () => {
        this.setState(
          {
            messageSent: false,
            messageError: false
          }
        )
      }

    sendMessage = () => {
        const formData = new FormData()
        formData.append(GOOGLE_FORM_MESSAGE_ID, this.state.message)
        formData.append(GOOGLE_FORM_EMAIL_ID, this.state.email)
        formData.append(GOOGLE_FORM_NAME_ID, this.state.name)
        
        axios.post(CORS_PROXY + GOOGLE_FORM_ACTION_URL, formData)
         .then(() => {
           this.setState({
            messageSent: true,
            sendingMessage: false,
            message: '',
            email: '',
            name: ''
          })
        }).catch(() => {
            this.setState({
              messageError: true,
              sendingMessage: false
            })
        })
      }

      returnButton = () => <button id='return-button' className='btn btn-default btn-xs' onClick={this.handleReturnButton}>Return</button>



    /* state = {
        contactInfo: {
            firstName: '',
            email: '',
            message: '',
        },        
        messagePosted: false
    }

    inputChangeHandler = (e) => {

        const updatedContactInfo = {...this.state.contactInfo}
        updatedContactInfo[e.target.name] = e.target.value


        this.setState({
            ...this.state,
            contactInfo: updatedContactInfo
        })
    } */

    /* onSubmitHandler = (e) => {
        e.preventDefault()

        // console.log(prevComments, 'Comment form')
        axios.post("/contactInfo.json", this.state.contactInfo)
            .then( res => { 
                // console.log("Successfull")
                this.setState({
                    messagePosted: true
                })
                this.props.history.push('/')
            })
            .catch( err => {
                console.log(err.message)
            })
    } */

    


    render () {
        if(this.state.sendingMessage){
            return(
              <div>Sending...</div>
            )
          }
          if(this.state.messageSent){
            return(
              <React.Fragment>
                <div className='success-message'>Sent! I will respond asap</div>
                {this.returnButton()}
              </React.Fragment>
            )
          }
          if(this.state.messageError){
            return(
              <React.Fragment>
                <div className='error-message'>Sorry, your message was not sent. Contact me at: leandrovicente.info@gmail.com</div>
                {this.returnButton()}
              </React.Fragment>
            )
          }
          if(this.state.showForm === false){
            return(
              <button id='contact-button' className='btn btn-sm' onClick={this.handleFormToggle}>Contact</button>
            )
          }
        
        return (
            // Contact section start
            <section className="contact">
                <div className="container">
                    <div className="row">
                        <div className="col-lg-8 col-md-10 m-auto">
                            <div className="sec-heading">
                            <h3 className="sec-title">Entre em contato</h3>
                            <p>Queremos te ouvir!</p>
                            </div>
                        </div>
                    </div>
                    <div className="row">
                        <div className="col-lg-10 col-md-12 m-auto">
                            <div className="row">
                                <div className="col-md-4">
                                    <address>
                                        <span className="ti-map-alt"></span>
                                        Rua Presidente João Pessoa 322 loja 105, Icaraí <br />
                                        Niterói, RJ
                                    </address>
                                    <address>
                                        <span className="ti-email"></span>
                                        <a href="mailto:example@leospa.com">contato@divasbeleza.com.br</a>
                                        <br/><br/>
                                    </address>
                                    <address>
                                        <span className="ti-tablet"></span>
                                        <a href="tel:+552136191304(02)">21 3619-1304</a>
                                        <a href="http://bit.ly/vemprodivas" target="blank">21 99227-7167</a>
                                    </address>
                                </div>
                                <div className="col-md-7 offset-md-1">
                                    <form className="contact-form" onSubmit={ this.handleSubmit }>
                                        <input 
                                            type="text" 
                                            name="firstName" 
                                            placeholder="Nome" 
                                            onChange={ this.handleChange}
                                            value={this.state.name} 
                                            
                                        />
                                        <input 
                                            type="email" 
                                            name="email" 
                                            placeholder="Email" 
                                            onChange={ this.handleChange }
                                            value={this.state.email}
                                            required
                                        />
                                        <textarea 
                                            name="message" 
                                            placeholder="Escreva sua mensagem..."
                                            onChange={ this.handleChange }
                                            value={this.state.message}
                                            required
                                        ></textarea>
                                        <button type="submit" className="btn btn-round">Enviar</button>
                                    </form>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </section>
            // Contact section end
        )
    }
}

export default Contact
