import React, { Component } from 'react'
import axios from '../axios-orders';
import OwlCarousel from 'react-owl-carousel';
import 'owl.carousel/dist/assets/owl.carousel.css';
import SingleTestimonial from '../Components/SingleTestimonial';

class Testimonial extends Component {

    state = {
        testimonials: [
            {
                id: 1,
                name: 'Endermoterapia',
                description: 'Ele é responsável por fazer uma sucção da pele, estimulando o sistema circulatório onde há celulite ou gordura localizada. Assim, elimina esses incômodos, dispensando cirurgias e evitando cicatrizes desnecessárias.',

            },
            {
                id: 2,
                name: 'Jato de Plasma',
                description: 'O tratamento tem a capacidade de melhorar manchas solares e de envelhecimento, estrias, cicatrizes, olheiras, retirada de pintas e verrugas, e amenizar linhas de expressão e flacidez.',

            },
            {
                id: 3,
                name: 'Clareamento de pele',
                description: 'Para acabar com o problema, o clareamento é a melhor solução! Seja no rosto, virilha ou axilas, o tratamento, que pode ser feito por meio de lasers, ácidos e até com mudanças na alimentação, deixa a autoestima lá em cima.',

            }            
        ]
    }

    render() {

        // console.log(this.state.testimonials)

        let testimonials = 'Loading...'
        if( this.state.testimonials.length) {
            testimonials = <OwlCarousel
                autoplay
                className="test-caro"
                dots={true}
                items={1}
                loop
                margin={0}
                nav={false}
            > 
            {
                this.state.testimonials.map( tsml => {
                    return (
                        <SingleTestimonial 
                            key={tsml.userId}
                            id={ tsml.id }
                            name={ tsml.name }
                            description={ tsml.description }
                        />
                    )
                })
            }
            </OwlCarousel>
        }
        

        return (
            // Testimonial section start 
            <section className="testimonial bg-lightred">
                <div className="container">
                    <div className="row">
                        <div className="col-lg-10 col-md-12 m-auto">
                            {testimonials}
                        </div>
                    </div>
                </div>
            </section>
            // Testimonial section end
        )
    }
}

export default Testimonial
