import React from 'react';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import CssBaseline from '@material-ui/core/CssBaseline';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import Link from '@material-ui/core/Link';
import './style.css';

import data from '../data/destaques';



const useStyles = makeStyles((theme) => ({
  heroContent: {
    /* backgroundImage: `url(${bg})`, */ 
    color: '#fff',
    paddingTop: theme.spacing(8),
    paddingBottom: theme.spacing(1),

    display: 'flex', 
    maxHeight: '400px',
    maxWidth: '100%',
    height: 'auto',
    width: 'auto',

  },
  heroButtons: {
    marginTop: theme.spacing(4),
  },
  cardGrid: {
    paddingTop: theme.spacing(8),
    paddingBottom: theme.spacing(8),
    width: '100%',

  },
  card: {
    height: '100%',
    display: 'flex',
    flexDirection: 'column',
  },
  cardMedia: {
    paddingTop: '56.25%', // 16:9

  },
  cardContent: {
    flexGrow: 1,
  },
  logo: {
    width: "200px",
    paddingTop: theme.spacing(3),
  },
  main: {
    background: '#fff',
  },
  name: {
    marginTop: '2px',
    height: '90px' ,
  },
  description: {
    marginTop: '1px',
    marginBottom: '40px',
    height: '100px' ,
  },
  price: {

  },

  footer: {
    background: '#fff',
    padding: theme.spacing(6),
    boxShadow: '0px -2px 7px 0px #999',
  },
}));



const cards = data;

export default function Highlights() {
  const classes = useStyles();

  return (
    <React.Fragment>
      <CssBaseline />
      <main className={classes.main}>

        <Container className={classes.cardGrid} maxWidth="md">

          <Grid container spacing={4} >
            {cards.map((card) => (
              <Grid item key={card} xs={12} sm={6} md={4}>
                <Card className={classes.card}>
                  <CardMedia
                    className={classes.cardMedia}
                    image={card.image}
                    title="Image title"
                    height="140px"

                  />
                  <CardContent className={classes.cardContent}>
                    <Typography gutterBottom variant="h6" component="h2" className={classes.name}>
                      {card.name}
                    </Typography>
                    <Typography gutterBottom variant="p" component="p" className={classes.description}>
                      <strong>{card.obs}</strong> <br/>
                      {card.description}
                    </Typography>
                    <Typography gutterBottom variant="p" component="h6" className={classes.price}>
                      <span className="price1">{card.price}</span>
                      <span className="price2">{card.price2}</span>
                    </Typography>
                  </CardContent>
                </Card>
              </Grid>
            ))}
          </Grid>
        </Container>
      </main>

    </React.Fragment>
  );
}
