import React, { Component } from 'react'
/* import axios from '../axios-orders' */

class Contact extends Component {

    /* state = {
        contactInfo: {
            firstName: '',
            email: '',
            message: '',
        },        
        messagePosted: false
    }

    inputChangeHandler = (e) => {

        const updatedContactInfo = {...this.state.contactInfo}
        updatedContactInfo[e.target.name] = e.target.value


        this.setState({
            ...this.state,
            contactInfo: updatedContactInfo
        })
    }

    onSubmitHandler = (e) => {
        e.preventDefault()

        // console.log(prevComments, 'Comment form')
        axios.post("/contactInfo.json", this.state.contactInfo)
            .then( res => { 
                // console.log("Successfull")
                this.setState({
                    messagePosted: true
                })
                this.props.history.push('/')
            })
            .catch( err => {
                console.log(err.message)
            })
    } */

    

        constructor(props) {
          super(props);
          this.state = { name: "", email: "", message: "" };
        }

        /* Here’s the juicy bit for posting the form submission */

        handleSubmit = e => {
          fetch("/", {
            method: "POST",
            headers: { "Content-Type": "application/x-www-form-urlencoded" },
            body: encode({ "form-name": "contact", ...this.state })
          })
            .then(() => alert("Success!"))
            .catch(error => alert(error));

          e.preventDefault();
        };

        handleChange = e => this.setState({ [e.target.name]: e.target.value });

        


    render () {
        
        return (
            // Contact section start
            <section className="contact">
                <div className="container">
                    <div className="row">
                        <div className="col-lg-8 col-md-10 m-auto">
                            <div className="sec-heading">
                            <h3 className="sec-title">Entre em contato</h3>
                            <p>Queremos te ouvir!</p>
                            </div>
                        </div>
                    </div>
                    <div className="row">
                        <div className="col-lg-10 col-md-12 m-auto">
                            <div className="row">
                                <div className="col-md-4">
                                    <address>
                                        <span className="ti-map-alt"></span>
                                        Rua Presidente João Pessoa 322 loja 105, Icaraí <br />
                                        Niterói, RJ
                                    </address>
                                    <address>
                                        <span className="ti-email"></span>
                                        <a href="mailto:example@leospa.com">contato@divasbeleza.com.br</a>
                                        <br/><br/>
                                    </address>
                                    <address>
                                        <span className="ti-tablet"></span>
                                        <a href="tel:+552136191304(02)">21 3619-1304</a>
                                        <a href="http://bit.ly/vemprodivas" target="blank">21 99227-7167</a>
                                    </address>
                                </div>
                                <div className="col-md-7 offset-md-1">
                                    <form className="contact-form" onSubmit={this.handleSubmit} >
                                        <input 
                                            type="text" 
                                            name="firstName" 
                                            placeholder="Nome" 
                                            value={name} 
                                            onChange={this.handleChange} 
                                            required
                                        />
                                        <input 
                                            type="email" 
                                            name="email" 
                                            placeholder="Email" 
                                            value={email} 
                                            onChange={this.handleChange}
                                            required
                                        />
                                        <textarea 
                                            name="message" 
                                            placeholder="Escreva sua mensagem..."
                                            value={message} 
                                            onChange={this.handleChange}
                                            required
                                        ></textarea>
                                        <button type="submit" className="btn btn-round">Enviar</button>
                                    </form>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </section>
            // Contact section end
        )
    }
}

export default Contact
